sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var eventGR = new GlideRecord('x_snc_marketing_0_event');
    var attendeeGr = new GlideRecord('x_snc_marketing_0_attendee');
    if (current.attendee_status == 'cancelled') {
        attendeeGr.addQuery('event_name', '=', current.event_name);
        attendeeGr.addQuery('attendee_status', '=', 'waitlisted');
        attendeeGr.orderBy('number');
        attendeeGr.query();
        if (attendeeGr.next()) {
            attendeeGr.attendee_status = 'confirmed';
            attendeeGr.update();
            var onHoldGR = new GlideRecord('x_snc_marketing_0_attendee');
            onHoldGR.addQuery('event_name', '=', current.event_name);
            onHoldGR.addQuery('attendee_status', '=', 'on_hold');
            onHoldGR.orderBy('number');
            onHoldGR.query();
            if (onHoldGR.next()) {
                onHoldGR.attendee_status = 'waitlisted';
                onHoldGR.update();
            }
        }
    } else {
        attendeeGr.addQuery('event_name', '=', current.event_name);
        attendeeGr.addQuery('attendee_status', '=', 'on_hold');
        attendeeGr.orderBy('number');
        attendeeGr.query();
        if (attendeeGr.next()) {
            attendeeGr.attendee_status = 'waitlisted';
            attendeeGr.update();
        }
    }
    // Get the event record
    if (eventGR.get(current.event_name)) {
        // Get counts for attendees then update Event
        var attendeeCount = new EventRegistrationAggregate().runAggregate('x_snc_marketing_0_attendee', 'attendee_status=confirmed^event_name=' + current.event_name, 'COUNT');
        eventGR.attendees = attendeeCount;
        // Get counts for waitlisted then update Event
        var waitlistedCount = new EventRegistrationAggregate().runAggregate('x_snc_marketing_0_attendee', 'attendee_status=waitlisted^event_name=' + current.event_name, 'COUNT');
        eventGR.waitlisted = waitlistedCount;
        // Save our changes to the event record
        eventGR.update();
    }

})(current, previous);
