sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    imports,
    helpers
}) {
    const ACTION_NAME = event.payload.action.actionDispatch;
    if (ACTION_NAME !== 'NAVIGATION') return;
    var table = event.payload.action.actionPayload.table;

    const fields = {
        "sysId": event.payload.action.actionPayload.sysId,
        "table": event.payload.action.actionPayload.table
    };
    debugger;
    helpers.navigate.to('event_details', fields);

}
