sys_ux_data_broker_graphql.query:
query ($id: String!, $getRootComments: Boolean!) {
  AppEvent_Query {
    getComments(id: $id, getRootComments: $getRootComments) {
      id
      created: createdOn
      text
      parent
      user {
        name
        id
        avatar
      }
      reactionCount: likesCount
      isReacted: isLiked
    }
  }
}

